import React from "react";
import "./Task.scss";

function Task(props) {
  console.log("isDone? : " + props.task.isDone);
  return (
    // <div className={"task" + (props.task.isDone ? " done" : "")}>
    <div className={"task" + (props.task.isDone ? " done" : "")}>

      <div className="left-text">
        <input
          type="checkbox"
          checked={props.task.isDone}
          // defaultChecked={props.task.isDone}
          onChange={() => {
            props.onCheck(props.task.id);
          }}
        />
        <p className="text">{props.task.name}</p>
      </div>

      <div className="btn-container">
        <p
          className="button"
          onClick={() => {
            props.onEdit(props.task.id);
          }}
        >
          Edit
        </p>
        <p
          className="button delete"
          onClick={() => {
            props.onDeleteTask(props.task.id);
          }}
        >
          Delete
        </p>
        <p
          className="button arrow"
          onClick={() => {
            props.moveUp(props.task.id);
          }}
        >
          ↑
        </p>
        <p
          className="button arrow"
          onClick={() => {
            props.moveDown(props.task.id);
          }}
        >
          ↓
        </p>
      </div>
    </div>
  );
}

export default Task;
