import React,{ Component } from "react";
import "./Todo.css";

export class Todo extends Component{
  constructor(props){
    super(props);
    this.state = {
      tasks : [],
      error : "",
      currValue : ""
    }
  }

  onChangeHandle = (e)=>{
    this.setState({currValue : e.target.value});
  }

  render(){
    return (
      <div className="container">
        <div className="input">
          <input type="text" 
                placeholder="Enter your task here..." 
                value={this.state.currValue} 
                onChange={this.onChangeHandle}
          />
          <button>Add</button>
        </div>
        <div className="error">
          <p className="error-text"> Please enter in a task </p>
          <p className="error-text"> The task already exists </p>
        </div>
        <div className="tasks-container">
          <div className="task">
            <input type="checkbox" 
                  // defaultChecked={this.state.chkbox} 
                  // onChange={this.handleChangeChk} 
                  />
            <div className="text">Task1</div>
            .btn-c
            <p className="button">Edit</p>
            <p className="button delete">Delete</p>
            <p className="button arrow">↑</p>
            <p className="button arrow">↓</p>
          </div>
        </div>
      </div>
    );
  }
}
