import React, { Component } from "react";
import "./Task.scss";

export class Task extends Component {
  render() {
    return (
      <div className={"task" + (this.props.task.isDone ? " done" : "")}>
        <div className="left-text">
          <input
            type="checkbox"
            defaultChecked={this.props.task.isDone}
            onChange={() => {
              this.props.onCheck(this.props.index);
            }}
          />
          <p className="text">{this.props.task.name}</p>
        </div>

        <div className="btn-container">
          <p
            className="button"
            onClick={() => {
              this.props.onEdit(this.props.index);
            }}
          >
            Edit
          </p>
          <p
            className="button delete"
            onClick={() => {
              this.props.onDeleteTask(this.props.index);
            }}
          >
            Delete
          </p>
          <p
            className="button arrow"
            onClick={() => {
              this.props.moveUp(this.props.index);
            }}
          >
            ↑
          </p>
          <p
            className="button arrow"
            onClick={() => {
              this.props.moveDown(this.props.index);
            }}
          >
            ↓
          </p>
        </div>
      </div>
    );
  }
}
