import React, { Component } from "react";
import "./Todo.css";

export class Todo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      error: "",
      currValue: "",
    };
  }

  onChangeHandle = (e) => {
    this.setState({ currValue: e.target.value });
  };

  onClickHandle = () =>{
    if(this.state.currValue === ""){
      this.setState({error: "Please enter in a task"})
    }
    else if(this.tasks.includes(this.state.currValue)){
      this.setState({error: "The task already exists"})
    }
    else{
      this.setState({
        tasks:[...this.state.tasks, { name: this.state.currValue, isDone:false }],
        error: ""
      })  

    }
  }

  render() {
    return (
      <div className="container">
        <div className="input">
          <input
            type="text"
            placeholder="Enter your task here..."
            value={this.state.currValue}
            onChange={this.onChangeHandle}
          />
          <button onClick={this.onClickHandle}>Add</button>
        </div>
        <div className="error">
          <p className="error-text"> {this.state.error} </p>
        </div>
        <div className="tasks-container">
          <div className="task">
            <div className="left-text">
              <input
                type="checkbox"
                // defaultChecked={this.state.chkbox}
                // onChange={this.handleChangeChk}
              />
              <p className="text">Task1</p>
            </div>

            <div className="btn-container">
              <p className="button">Edit</p>
              <p className="button delete">Delete</p>
              <p className="button arrow">↑</p>
              <p className="button arrow">↓</p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
