import React,{ Component } from "react";


export class Todo extends Component{
  constructor(props){
    super(props);
    this.state = {
      tasks : [],
      error : "",
      currValue : ""
    }
  }

  render(){
    return (
      <div className="container">
        <div className="input">
          <input type="text" 
          placeholder="Enter your task here: " 
          value={this.state} />
        </div>
        <div className="error"></div>
        <div className="tasks-container"></div>
      </div>
    );
  }
}
