import React, { useState } from "react";
import "./Todo.scss";
import { Error } from "./Error";
import Task from "./Task";

export function Todo() {
  const [tasks, setTasks] = useState([]);

  const [error, setError] = useState("");
  const [editError, setEditError] = useState("");

  const [currValue, setCurrValue] = useState("");
  const [editValue, setEditValue] = useState("");

  const [editMode, setEditMode] = useState(false);

  const [currIndex, setCurrIndex] = useState(null);

  onChangeHandle = (e) => {
    setCurrValue(e.target.value);
  };

  isValueValid = (value) => {
    if (value === "") {
      editMode
        ? setEditError("Please enter in a task")
        : setError("Please enter in a task");

      return false;
    } else if (
      !editMode &&
      tasks.length !== 0 &&
      tasks.some((el) => el.name === value)
    ) {
      setError("The task already exists");

      return false;
    }

    return true;
  };

  onClickHandle = () => {
    if (this.isValueValid(currValue)) {
      console.log(currValue);
      // this.setState({
      //   tasks: [
      //     ...tasks,
      //     { name: currValue, isDone: false },
      //   ],
      //   currValue: "",
      //   error: "",
      // });

      setTasks([...tasks, { name: currValue, isDone: false }]);
      setCurrValue("");
      setError("");
    }
  };

  onDeleteAll = () => {
    setTasks([]);
    // this.setState({
    //   tasks: [],
    // });
  };

  onDeleteDone = () => {
    // this.setState({
    //   tasks: tasks.filter((el) => el.isDone === false),
    // });

    setTasks(tasks.filter((el) => el.isDone === false)) ;
  };

  onDeleteNotDone = () => {
    // this.setState({
    //   tasks: tasks.filter((el) => el.isDone === true),
    // });

    setTasks(tasks.filter((el) => el.isDone === true));
  };

  onDeleteTask = (index) => {
    // this.setState({
    //   tasks: tasks.filter((el, i) => i !== index),
    // });

    setTasks(tasks.filter((el, i) => i !== index));
  };

  onCheck = (index) => {
    let newTasks = tasks;
    newTasks[index].isDone = !tasks[index].isDone;
    // this.setState({
    //   tasks: newTasks,
    // });

    setTasks(newTasks);

    // [
    //   ...newTasks.filter((el) => el.isDone === false),
    //   ...newTasks.filter((el) => el.isDone === true),
    // ]
  };

  moveDown = (index) => {
    let swapIndex = index + 1;
    if (swapIndex === tasks.length) {
      swapIndex = 0;
    }
    this.move(index, swapIndex);
  };

  moveUp = (index) => {
    let swapIndex = index - 1;
    if (index === 0) {
      swapIndex = tasks.length - 1;
    }
    this.move(index, swapIndex);
  };

  move = (index, swapIndex) => {
    let temp = tasks[index];
    let tempTasks = tasks;
    tempTasks[index] = tempTasks[swapIndex];
    tempTasks[swapIndex] = temp;

    // this.setState({
    //   tasks: tempTasks,
    // });

    setTasks(tempTasks);
  };

  onEdit = (index) => {
    // this.setState({
    //   editMode: true,
    //   currIndex: index,
    //   editValue: tasks[index].name,
    // });

    setEditMode(true);
    setCurrIndex(index);
    setEditValue(tasks[index].name)
  };

  onEditHandle = (e) => {
    this.setState({
      editValue: e.target.value,
    });


  };

  onEditClick = () => {
    if (this.isValueValid(editValue)) {
      let newTasks = tasks;
      newTasks[currIndex].name = editValue;
      this.setState({
        editMode: false,
        currIndex: null,
        editValue: "",
        tasks: newTasks,
        editError: "",
      });
    }
  };

  console.log(tasks);
  return (
    <div className="container">
      <div className="input">
        <input
          type="text"
          placeholder="Enter your task here..."
          value={currValue}
          onChange={this.onChangeHandle}
        />
        <button onClick={this.onClickHandle}>Add</button>
      </div>
      <Error error={error} />
      <div className="additianal-btns">
        <button className="btn-2" onClick={this.onDeleteAll}>
          Delete All
        </button>
        <button className="btn-2" onClick={this.onDeleteDone}>
          Delete Done
        </button>
        <button className="btn-2" onClick={this.onDeleteNotDone}>
          Delete Not Done
        </button>
      </div>
      <div className="tasks-container">
        {editMode === true && (
          <div className="black">
            <div className="overlay">
              <input
                type="text"
                value={editValue}
                onChange={this.onEditHandle}
              />
              <Error error={editError} />
              <button onClick={this.onEditClick}>Edit</button>
              <button
                className="btn-2"
                onClick={() => {
                  this.setState({ editMode: false, editError: "" });
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        )}

        <ul>
          {tasks.map((task, index) => (
            <li key={"task" + index}>
              <Task
                task={task}
                index={index}
                onCheck={(e) => this.onCheck(e)}
                onEdit={(e) => this.onEdit(e)}
                onDeleteTask={(e) => this.onDeleteTask(e)}
                moveUp={(e) => {
                  this.moveUp(e);
                }}
                moveDown={(e) => {
                  this.moveDown(e);
                }}
              />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
