import React, { useState } from "react";
import "./Todo.scss";
import { Error } from "./Error";
import Task from "./Task";

export function Todo() {
  const [tasks, setTasks] = useState([]);

  const [error, setError] = useState("");
  const [editError, setEditError] = useState("");

  const [currValue, setCurrValue] = useState("");
  const [editValue, setEditValue] = useState("");

  const [editMode, setEditMode] = useState(false);

  const [currIndex, setCurrIndex] = useState(null);

  function isValueValid(value) {
    if (value === "") {
      editMode
        ? setEditError("Please enter in a task")
        : setError("Please enter in a task");

      return false;
    } else if (
      !editMode &&
      tasks.length !== 0 &&
      tasks.some((el) => el.name === value)
    ) {
      setError("The task already exists");

      return false;
    }

    return true;
  }

  const onClickHandle = () => {
    if (isValueValid(currValue)) {
      setTasks([...tasks, { name: currValue, isDone: false }]);
      setCurrValue("");
      setError("");
    }
  };

  const onDeleteAll = () => {
    setTasks([]);
  };

  const onDeleteDone = () => {
    setTasks(tasks.filter((el) => el.isDone === false));
  };

  const onDeleteNotDone = () => {
    setTasks(tasks.filter((el) => el.isDone === true));
  };

  const onDeleteTask = (index) => {
    setTasks(tasks.filter((el, i) => i !== index));
  };

  const onCheck = (index) => {
    let newTasks = tasks;
    newTasks[index].isDone = !tasks[index].isDone;
    setTasks(newTasks);

    // [
    //   ...newTasks.filter((el) => el.isDone === false),
    //   ...newTasks.filter((el) => el.isDone === true),
    // ]
  };

  const moveDown = (index) => {
    let swapIndex = index + 1;
    if (swapIndex === tasks.length) {
      swapIndex = 0;
    }
    move(index, swapIndex);
  };

  const moveUp = (index) => {
    let swapIndex = index - 1;
    if (index === 0) {
      swapIndex = tasks.length - 1;
    }
    move(index, swapIndex);
  };

  const move = (index, swapIndex) => {
    let temp = tasks[index];
    let tempTasks = tasks;
    tempTasks[index] = tempTasks[swapIndex];
    tempTasks[swapIndex] = temp;

    setTasks(tempTasks);
  };

  const onEdit = (index) => {
    setEditMode(true);
    setCurrIndex(index);
    setEditValue(tasks[index].name);
  };

  const onEditHandle = (e) => {
    setEditValue(e.target.value);
  };

  const onEditClick = () => {
    if (isValueValid(editValue)) {
      let newTasks = tasks;
      newTasks[currIndex].name = editValue;


      setEditMode(false);
      setCurrIndex(null);
      setEditValue("");
      setTasks(newTasks);
      setEditError("");
    }
  };

  return (
    <div className="container">
      <div className="input">
        <input
          type="text"
          placeholder="Enter your task here..."
          value={currValue}
          onChange={(e) => {
            setCurrValue(e.target.value);
          }}
        />
        <button onClick={onClickHandle}>Add</button>
      </div>
      <Error error={error} />
      <div className="additianal-btns">
        <button className="btn-2" onClick={onDeleteAll}>
          Delete All
        </button>
        <button className="btn-2" onClick={onDeleteDone}>
          Delete Done
        </button>
        <button className="btn-2" onClick={onDeleteNotDone}>
          Delete Not Done
        </button>
      </div>
      <div className="tasks-container">
        {editMode === true && (
          <div className="black">
            <div className="overlay">
              <input type="text" value={editValue} onChange={onEditHandle} />
              <Error error={editError} />
              <button onClick={onEditClick}>Edit</button>
              <button
                className="btn-2"
                onClick={() => {
                  setEditMode(false);
                  setEditError("");
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        )}

        <ul>
          {tasks.map((task, index) => (
            <li key={"task" + index}>
              <Task
                task={task}
                index={index}
                onCheck={(e) => onCheck(e)}
                onEdit={(e) => onEdit(e)}
                onDeleteTask={(e) => onDeleteTask(e)}
                moveUp={(e) => {
                  moveUp(e);
                }}
                moveDown={(e) => {
                  moveDown(e);
                }}
              />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
